import React, { Component } from 'react';
import Home from './Components/Home';
import About from './Components/About';
import LatestCollection from './Components/LatestCollection';
import './App.css';
import './SASS/main.scss';

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      location: 'home'
    };
  }

  activeChangeHandler = changeTo => {
    if (changeTo === 'home') {
      this.props.history.push('/home');
    } else if (changeTo === 'about') {
      this.props.history.push('/about');
    } else if (changeTo === 'contact') {
      this.props.history.push('/contact');
    }
    this.setState({
      location: changeTo
    });
  };

  renderHome = () => {
    return <Home activeChangeHandler={this.activeChangeHandler} />;
  };

  renderAbout = () => {
    return <About activeChangeHandler={this.activeChangeHandler} />;
  };

  componentDidUpdate() {
    const { page } = this.props;
    const { location } = this.state;
    console.log(page, location);
    if (page && location !== page) {
      this.setState({ location: page });
    }
  }

  render() {
    const { location } = this.state;
    return (
      <div>
        {location === 'home' && this.renderHome()}
        {location === 'about' && this.renderAbout()}
        <LatestCollection />
      </div>
    );
  }
}
