import React, { Component } from 'react';
import Home from './Components/Home';
import About from './Components/About';
import LatestCollection from './Components/LatestCollection';
import './App.css';

import './SASS/main.scss';

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      location: 'home'
    };
  }

  renderCurrentPage = () => {
    this.state.location === 'home' ? (
      <Home changePageHandler={this.changePageHandler} />
    ) : null;
    if (this.state.location === 'about') {
      return <About activeChangeHandler={this.activeChangeHandler} />;
    }
  };

  activeChangeHandler = changeTo => {
    if (changeTo === 'home') {
      this.props.history.push('/home');
    } else if (changeTo === 'about') {
      this.props.history.push('/about');
    } else if (changeTo === 'contact') {
      this.props.history.push('/contact');
    }
    this.setState({
      location: changeTo
    });
  };

