import React, { Component } from 'react';
import Home from './Components/Home';
import About from './Components/About';
import './App.css';
import './SASS/main.scss';

import gallery from '../images/gallery.png';
import logo from './images/juried logo-01.svg';

export default class App extends Component {
  activeChangeHandler = changeTo => {
    if (changeTo === 'home') {
      this.props.history.push('/home');
    } else if (changeTo === 'about') {
      this.props.history.push('/about');
    } else if (changeTo === 'contact') {
      this.props.history.push('/contact');
    }
  };

  renderHome = () => {
    return <Home activeChangeHandler={this.activeChangeHandler} />;
  };

  renderAbout = () => {
    return <About activeChangeHandler={this.activeChangeHandler} />;
  };

  render() {
    const { pathname } = this.props.history.location;
    return (
      <div>
        <div className='aboveFold_photo'>
          <img className='aboveFold_photo-logo' src={logo} alt='logo' />
          <img className='aboveFold_photo-img' src={gallery} alt='gallery' />
          <div className='aboveFold_photo-dummy'></div>
        </div>
        {pathname === '/home' && this.renderHome()}
        {pathname === '/about' && this.renderAbout()}
      </div>
    );
  }
}
